package com.rui.xb.purple.mvp.model.home;

import com.rui.xb.purple.mvp.base.BaseMVPModule;
import com.rui.xb.purple.utils.UrlRoute;

import java.util.HashMap;
import java.util.Map;

import javax.inject.Inject;

import io.reactivex.disposables.Disposable;
import io.reactivex.functions.Consumer;

/**
 * Created by Rui on 2018/7/4.
 */

public class HomeModel extends BaseMVPModule{

    @Inject
    public HomeModel() {
    }


    public Disposable login(Consumer<String> success,Consumer<Throwable> fail){
        Map<String,Object> map = new HashMap<>();
        map.put("param","");
        return requestByPostJson(UrlRoute.LOGIN,map).subscribe(success,fail);
    }

    public Disposable logout(Consumer<String> success,Consumer<Throwable> fail){
        Map<String,Object> map = new HashMap<>();
        map.put("param","");
        return requestByGet(UrlRoute.LOGOUT,map).subscribe(success,fail);
    }


}
